// =============================================================================
// FVE Konfigurace - V√Ωchoz√≠ hodnoty
// =============================================================================

const config = {
    // Baterie
    kapacita_baterie_kwh: 28,
    min_soc: 20,
    max_daily_soc: 80,
    amortizace_baterie_czk_kwh: 1.5,
    charge_efficiency: 0.90,       // 10% ztr√°ta p≈ôi nab√≠jen√≠
    discharge_efficiency: 0.90,    // 10% ztr√°ta p≈ôi vyb√≠jen√≠
    charge_rate_kwh: 5,            // rychlost nab√≠jen√≠ ze s√≠tƒõ (kWh/h)
    soc_drop_normal_pct: 5,        // % pokles SOC za hodinu v NORMAL m√≥du
    daily_consumption_kwh: 20,     // odhadovan√° denn√≠ spot≈ôeba domu (kWh)

    // S√≠≈•
    max_feed_in_w: 7600,
    max_spotreba_sit_w: 22000,
    safety_margin_w: 2000,
    solar_start_hour: 9,
    solar_end_hour: 17,
    soc_drop_setrit_pct: 1,
    jistic_a: 32,

    // FVE
    fve_peak_kwp: 3,
    fve_azimut: 270,
    fve_sklon: 50,

    // Victron MQTT
    victron_id: "c0619ab69c71",
    victron_mqtt_prefix: "victron",

    // Cenov√© prahy (level 1-10, 1=nejlevnƒõj≈°√≠)
    prah_levna_energie: 4,      // level <= prah = levn√° energie
    prah_draha_energie: 12,      // level >= prah = drah√° energie

    // Hystereze
    hystereze_zapnuti_w: 500,   // p≈ôebytek pro zapnut√≠ spot≈ôebiƒçe
    hystereze_vypnuti_w: 200,   // p≈ôebytek pro udr≈æen√≠ spot≈ôebiƒçe

    // ƒåasov√© intervaly
    plan_interval_min: 1,      // interval aktualizace pl√°nu
    plan_horizon_hours: 12,     // horizont pl√°nov√°n√≠

    // Manu√°ln√≠ m√≥d (auto = automatick√Ω v√Ωbƒõr podle pl√°nu)
    manual_mod: "auto",

    // Automatizace
    automatizace_enabled: true,
    prodej_z_baterie_enabled: false,
    blokace_vybijeni: false,

    // M√≥dy
    mody: {
        NORMAL: "normal",
        SETRIT: "setrit",
        NABIJET_ZE_SITE: "nabijet_ze_site",
        PRODAVAT: "prodavat",
        PRODAVAT_MISTO_NABIJENI: "prodavat_misto_nabijeni",
        ZAKAZ_PRETOKU: "zakaz_pretoku"
    },

    // Priorita spot≈ôebiƒç≈Ø (ni≈æ≈°√≠ = vy≈°≈°√≠ priorita)
    priority: {
        tepelne_cerpadlo: 1,
        bojler: 2,
        auto_nabijeni: 3,       
        baterie_nabijeni: 4,
        ostatni: 5
    }
};

// Ulo≈æit do global kontextu
global.set("fve_config", config);

// Ulo≈æit jednotliv√© hodnoty pro zpƒõtnou kompatibilitu
global.set("max_spotreba_sit", config.max_spotreba_sit_w);
global.set("forecast_vyroba_dnes", 0);
global.set("forecast_vyroba_zitra", 0);

msg.payload = config;
msg.topic = "fve/config";

node.status({fill:"green", shape:"dot", text:"Konfigurace naƒçtena"});

return msg;

// Zpracov√°n√≠ cenov√Ωch dat z datab√°ze - aktualizace ka≈ædou minutu
// Deduplikace: pokud tabulka obsahuje data z v√≠ce dn√≠,
// zachov√°me posledn√≠ (nejaktu√°lnƒõj≈°√≠) z√°znam pro ka≈ædou hodinu
const prices = msg.payload || [];
const config = global.get("fve_config") || {};
const rezim = config.rezim_casu || "hodina";

const now = new Date();
const currentHour = now.getHours();
const currentMinute = now.getMinutes();
const currentQuarter = Math.floor(currentMinute / 15);

// Deduplikace po hodin√°ch - pozdƒõj≈°√≠ z√°znamy p≈ôep√≠≈°√≠ star≈°√≠
var priceMap = {};
for (var i = 0; i < prices.length; i++) {
    var row = prices[i];
    var key = String(row.hour);
    if (rezim === "ctvrthodina" && row.minute !== undefined) {
        key = row.hour + "_" + row.minute;
    }
    priceMap[key] = row;
}
var dedupedPrices = [];
var keys = Object.keys(priceMap);
for (var k = 0; k < keys.length; k++) {
    dedupedPrices.push(priceMap[keys[k]]);
}

// Naj√≠t aktu√°ln√≠ cenu
let currentPrice = null;
let priceLevel = 5; // default st≈ôedn√≠

for (const row of dedupedPrices) {
    if (row.hour === currentHour) {
        if (rezim === "ctvrthodina") {
            if (row.minute === currentQuarter * 15) {
                currentPrice = {
                    buy: row.priceCZKquarterBuy,
                    sell: row.priceCZKquarterProd,
                    levelBuy: row.levelCheapestQuarterBuy,
                    levelSell: row.levelMostExpensiveQuarterProd
                };
                priceLevel = row.levelCheapestQuarterBuy;
                break;
            }
        } else {
            currentPrice = {
                buy: row.priceCZKhourBuy,
                sell: row.priceCZKhourProd,
                levelBuy: row.levelCheapestHourBuy,
                levelSell: row.levelMostExpensiveHourProd
            };
            priceLevel = row.levelCheapestHourBuy;
            break;
        }
    }
}

// Ulo≈æit do global kontextu
global.set("fve_current_price", currentPrice);
global.set("fve_price_level", priceLevel);
global.set("fve_prices_forecast", dedupedPrices);

msg.payload = {
    currentPrice: currentPrice,
    priceLevel: priceLevel,
    pricesCount: dedupedPrices.length,
    rawCount: prices.length,
    timestamp: now.toISOString()
};

const levelText = priceLevel <= 4 ? "levn√°" : (priceLevel >= 7 ? "drah√°" : "st≈ôedn√≠");
node.status({fill: priceLevel <= 4 ? "green" : (priceLevel >= 7 ? "red" : "yellow"), shape:"dot", text: `Level ${priceLevel} (${levelText}) | ${dedupedPrices.length}/${prices.length} ≈ô√°dk≈Ø`});

return msg;

// Sestaven√≠ aktu√°ln√≠ho stavu syst√©mu
const config = global.get("fve_config") || {};
const priceLevel = global.get("fve_price_level") || 5;
const currentPrice = global.get("fve_current_price") || {};

const status = {
    timestamp: new Date().toISOString(),

    // Baterie
    battery_soc: msg.battery_soc || 0,
    battery_energy_kwh: (msg.battery_soc / 100) * (config.kapacita_baterie_kwh || 28),
    battery_available_kwh: Math.max(0, ((msg.battery_soc - (config.min_soc || 20)) / 100) * (config.kapacita_baterie_kwh || 28)),
    battery_free_kwh: ((100 - msg.battery_soc) / 100) * (config.kapacita_baterie_kwh || 28),

    // Ceny
    price_level: priceLevel,
    price_buy: currentPrice.buy || 0,
    price_sell: currentPrice.sell || 0,
    is_cheap: priceLevel <= (config.prah_levna_energie || 4),
    is_expensive: priceLevel >= (config.prah_draha_energie || 7),

    // Konfigurace
    automatizace_enabled: config.automatizace_enabled !== false,
    prodej_z_baterie_enabled: config.prodej_z_baterie_enabled === true,
    blokace_vybijeni: config.blokace_vybijeni === true,
    manual_mod: config.manual_mod || "auto"
};

// Ulo≈æit do global kontextu
global.set("fve_status", status);

msg.payload = status;

const socText = `SOC: ${status.battery_soc}% | Level: ${status.price_level}`;
node.status({fill: status.battery_soc > 50 ? "green" : (status.battery_soc > 20 ? "yellow" : "red"), shape:"dot", text: socText});

return msg;

// Naƒçten√≠ aktu√°ln√≠ konfigurace
const config = global.get("fve_config") || {};

// Aktualizace z HA entity (pokud p≈ôi≈°la zmƒõna)
const entity_id = msg.entity_id || "";
const value = msg.payload;

switch(entity_id) {
    case "input_number.fve_kapacita_baterie":
        config.kapacita_baterie_kwh = parseFloat(value) || 28;
        break;
    case "input_number.fve_min_soc":
        config.min_soc = parseFloat(value) || 20;
        break;
    case "input_number.fve_amortizace_baterie":
        config.amortizace_baterie_czk_kwh = parseFloat(value) || 1.0;
        break;
    case "input_number.fve_max_feed_in":
        config.max_feed_in_w = parseFloat(value) || 7600;
        break;
    case "input_number.fve_max_spotreba_sit":
        config.max_spotreba_sit_w = parseFloat(value) || 22000;
        global.set("max_spotreba_sit", config.max_spotreba_sit_w);
        break;
    case "input_boolean.fve_automatizace":
        config.automatizace_enabled = (value === "on");
        break;
    case "input_boolean.fve_prodej_z_baterie":
        config.prodej_z_baterie_enabled = (value === "on");
        break;
    case "input_boolean.fve_blokace_vybijeni":
        config.blokace_vybijeni = (value === "on");
        break;
    case "input_boolean.letni_rezim":
        config.letni_rezim = (value === "on");
        break;
    case "input_number.predpoved_solarni_vyroby_dnes":
        config.forecast_vyroba_dnes = parseFloat(value) || 0;
        global.set("forecast_vyroba_dnes", config.forecast_vyroba_dnes);
        break;
    case "input_number.predpoved_solarni_vyroby_zitra":
        config.forecast_vyroba_zitra = parseFloat(value) || 0;
        global.set("forecast_vyroba_zitra", config.forecast_vyroba_zitra);
        break;
    case "input_number.zbyvajici_solarni_vyroba_dnes":
        config.zbyvajici_solar_dnes = parseFloat(value) || 0;
        global.set("zbyvajici_solar_dnes", config.zbyvajici_solar_dnes);
        break;
    case "input_select.fve_rezim_casu":
        config.rezim_casu = value;
        break;
    case "input_select.fve_manual_mod":
        // Mapov√°n√≠ ƒçesk√Ωch n√°zv≈Ø na technick√© hodnoty
        const modeMap = {
            "ü§ñ Automaticky": "auto",
            "üü¢ Norm√°ln√≠ provoz": "normal",
            "üü° ≈†et≈ôit baterii": "setrit",
            "üîµ Nab√≠jet ze s√≠tƒõ": "nabijet_ze_site",
            "üî¥ Prod√°vat do s√≠tƒõ": "prodavat",
            "üü£ Z√°kaz p≈ôetok≈Ø": "zakaz_pretoku"
        };
        config.manual_mod = modeMap[value] || value;
        break;
}

// Ulo≈æit aktualizovanou konfiguraci
global.set("fve_config", config);

msg.payload = {
    entity_id: entity_id,
    value: value,
    config: config
};

node.status({fill:"blue", shape:"dot", text: entity_id.split(".")[1] + ": " + value});

return msg;
