var config = global.get("fve_config") || {};
var status = global.get("fve_status") || {};
var minSoc = config.min_soc || 20;
var currentSoc = status.battery_soc || 50;
var cerpadloTopi = msg.cerpadloTopi || false;
var autoCharging = msg.autoNabijeniAktivni || false;

var blockDischarge = cerpadloTopi || autoCharging;

if (blockDischarge) {
    // ČERPADLO TOPÍ nebo AUTO NABÍJÍ:
    // Zamknout baterii na aktuálním SOC - MUSÍ aktivovat scheduled charging!
    // Bez schedule_charge_duration > 0 se scheduled_soc IGNORUJE!
    // Solár → spotřeba (čerpadlo), přebytek → síť (prodej)
    // Deficit → import ze sítě
    msg.victron = {
        power_set_point: 0,
        min_soc: minSoc,
        schedule_soc: currentSoc,
        schedule_charge_duration: 86399,
        schedule_charge_day: 7
    };
} else {
    // NORMÁLNÍ PROVOZ:
    // Deaktivovat scheduled charging, ESS normálně řídí
    msg.victron = {
        power_set_point: 0,
        min_soc: minSoc,
        schedule_soc: 0,
        schedule_charge_duration: 0,
        schedule_charge_day: -7
    };
}

msg.mode = "normal";
var consumers = [];
if (cerpadloTopi) consumers.push("Topeni");
if (autoCharging) consumers.push("Auto");
var consText = consumers.length > 0 ? " + " + consumers.join("+") : "";
var lockText = blockDischarge ? " (BAT LOCKED)" : "";
node.status({fill:"green", shape:"dot", text:"Normal" + consText + " | SOC: " + currentSoc + "%" + lockText});

global.set("energy_arbiter", {
    mode: "normal",
    battery_charging: !blockDischarge,
    reserved_for_battery_w: 0,
    max_discharge_allowed: !blockDischarge,
    consumers_active: consumers,
    timestamp: Date.now()
});

msg.blockDischarge = blockDischarge;
msg.maxDischargePower = blockDischarge ? 0 : -1;
msg.maxChargePower = blockDischarge ? 0 : -1;

return msg;

var config = global.get("fve_config") || {};
var status = global.get("fve_status") || {};
var currentSoc = status.battery_soc || 50;
var minSoc = config.min_soc || 20;
var cerpadloTopi = msg.cerpadloTopi || false;
var autoCharging = msg.autoNabijeniAktivni || false;

// ŠETŘIT: Baterie zamčená na aktuálním SOC
// MUSÍ aktivovat scheduled charging aby scheduled_soc fungoval!
msg.victron = {
    power_set_point: 0,
    min_soc: minSoc,
    schedule_soc: currentSoc,
    schedule_charge_duration: 86399,
    schedule_charge_day: 7
};
msg.mode = "setrit";

var consumers = [];
if (cerpadloTopi) consumers.push("Topeni");
if (autoCharging) consumers.push("Auto");
var consText = consumers.length > 0 ? " | " + consumers.join("+") : "";
node.status({fill:"yellow", shape:"dot", text:"Setrit" + consText + " | SOC:" + currentSoc + "% (BAT LOCKED)"});

global.set("energy_arbiter", {
    mode: "setrit",
    battery_charging: false,
    battery_charge_rate_w: 0,
    power_set_point: 0,
    consumers_active: consumers,
    max_discharge_allowed: false,
