});

return msg;

var config = global.get("fve_config") || {};
var minSoc = config.min_soc || 20;
var status = global.get("fve_status") || {};
var currentSoc = status.battery_soc || 50;
var maxGridW = config.max_spotreba_sit_w || 22000;
var SAFETY_MARGIN = config.safety_margin_w || 2000;
var cerpadloTopi = msg.cerpadloTopi || false;
var autoCharging = msg.autoNabijeniAktivni || false;

var consumers = [];
if (cerpadloTopi) consumers.push("Topeni");
if (autoCharging) consumers.push("Auto");

if (cerpadloTopi || autoCharging) {
    // HIGH-PRIORITY CONSUMER ACTIVE
    // Scheduled charging ON, ale s omezeným importem (safety margin)
    var safeImport = maxGridW - SAFETY_MARGIN;
    msg.victron = {
        power_set_point: safeImport,
        schedule_charge_soc: 100,
        schedule_charge_duration: 86399,
        schedule_charge_day: 7,
        min_soc: minSoc
    };
    node.status({fill:"blue", shape:"dot",
        text:"Nabijet+" + consumers.join("+") + " | PSP:" + safeImport + "W | SOC:" + currentSoc + "%"});
} else {
    // NO HIGH-PRIORITY CONSUMER
    msg.victron = {
        power_set_point: -maxGridW,
        schedule_charge_soc: 100,
        schedule_charge_duration: 86399,
        schedule_charge_day: 7,
        min_soc: minSoc
    };
    node.status({fill:"blue", shape:"dot",
        text:"Nabijet | SCHED:100% | PSP:-" + maxGridW + "W | SOC:" + currentSoc + "%"});
}
msg.mode = "nabijet_ze_site";

global.set("energy_arbiter", {
    mode: "nabijet_ze_site",
    battery_charging: true,
    scheduled_charging: true,
    power_set_point: msg.victron.power_set_point,
    consumers_active: consumers,
    max_discharge_allowed: false,
    timestamp: Date.now()
});

return msg;

var config = global.get("fve_config") || {};
var status = global.get("fve_status") || {};

// Při nabíjení auta neprodáváme z baterie
if (msg.autoNabijeniAktivni) {
    node.status({fill:"yellow", shape:"ring", text:"Skip prodej - auto se nabíjí"});
    return null;
}

var currentSoc = status.battery_soc || 50;
var minSoc = config.min_soc || 20;
var maxFeedIn = config.max_feed_in_w || 7600;

if (currentSoc <= minSoc + 1) {
    node.status({fill:"red", shape:"ring", text:"Nedostatek energie"});
    return null;
}

msg.victron = { power_set_point: -maxFeedIn, min_soc: minSoc };
msg.mode = "prodavat";
node.status({fill:"red", shape:"dot", text:"Prodávat | " + maxFeedIn + "W | SOC: " + currentSoc + "%"});

// === ENERGY ARBITER ===
var consumers = [];
if (msg.cerpadloTopi) consumers.push("Topeni");
if (msg.autoNabijeniAktivni) consumers.push("Auto");

global.set("energy_arbiter", {
    mode: "prodavat",
    battery_charging: false,
    reserved_for_battery_w: 0,
    max_discharge_allowed: true,
    consumers_active: consumers,
    timestamp: Date.now()
});

msg.maxChargePower = 0;

return msg;

var config = global.get("fve_config") || {};
var status = global.get("fve_status") || {};
var currentSoc = status.battery_soc || 50;
var minSoc = config.min_soc || 20;

msg.victron = { power_set_point: 0, min_soc: minSoc };
msg.mode = "zakaz_pretoku";
var autoText = msg.autoNabijeniAktivni ? " + Auto" : "";
node.status({fill:"purple", shape:"dot", text:"Zákaz přetoků" + autoText + " | SOC: " + currentSoc + "%"});

// === ENERGY ARBITER ===
var consumers = [];
