var patBlockCharge = global.get("pat_block_charge") || false;
var config = global.get("fve_config") || {};
var minSoc = config.min_soc || 20;
var status = global.get("fve_status") || {};
var currentSoc = status.battery_soc || 50;
var maxGridW = config.max_spotreba_sit_w || 22000;
var SAFETY_MARGIN = config.safety_margin_w || 2000;
var cerpadloTopi = msg.cerpadloTopi || false;
var autoCharging = msg.autoNabijeniAktivni || false;
var saunaAktivni = msg.saunaAktivni || false;

var plan = global.get("fve_plan") || {};
var targetSoc = (plan && plan.smartCharging) ? plan.smartCharging.targetSocFromGrid : 100;

var consumers = [];
if (cerpadloTopi) consumers.push("Topeni");
if (autoCharging) consumers.push("Auto");
if (saunaAktivni) consumers.push("Sauna");

var psp, statusText;
if (cerpadloTopi || autoCharging) {
    var safeImport = maxGridW - SAFETY_MARGIN;
    psp = safeImport;
    statusText = "Nabijet+" + consumers.join("+") + " | PSP:" + safeImport + "W | SOC:" + currentSoc + "%";
} else {
    psp = -maxGridW;
    statusText = "Nabijet | SCHED:" + targetSoc + "% | PSP:-" + maxGridW + "W | SOC:" + currentSoc + "%";
}

msg.victron = {
    power_set_point: psp,
    min_soc: minSoc,
    schedule_soc: targetSoc,
    schedule_charge_duration: 86399,
    schedule_charge_day: 7,
    max_discharge_power: -1,
    max_charge_power: patBlockCharge ? 0 : -1,
    blockMinSoc: minSoc,
    feedin_on: true,
    max_feed_in_power: 7600,
    prevent_feedback: 0
};
msg.mode = "nabijet_ze_site";
node.status({fill:"blue", shape:"dot", text: statusText});

global.set("energy_arbiter", {
    mode: "nabijet_ze_site",
    battery_charging: true,
    battery_charge_rate_w: 5000,
    power_set_point: psp,
    consumers_active: consumers,
    max_discharge_allowed: false,
    timestamp: Date.now()
});

return msg;

var patBlockCharge = global.get("pat_block_charge") || false;
var saunaAktivni = msg.saunaAktivni || false;
var config = global.get("fve_config") || {};
var status = global.get("fve_status") || {};

if (msg.autoNabijeniAktivni) {
    node.status({fill:"yellow", shape:"ring", text:"Skip prodej - auto se nab├şj├ş"});
    return null;
}

var currentSoc = status.battery_soc || 50;
var minSoc = config.min_soc || 20;
var maxFeedIn = config.max_feed_in_w || 7600;

if (currentSoc <= minSoc + 1) {
    node.status({fill:"red", shape:"ring", text:"Nedostatek energie"});
    return null;
}

var effectiveMinSoc = Math.max(minSoc, currentSoc - 1);

msg.victron = {
    power_set_point: 0,
    min_soc: effectiveMinSoc,
    schedule_soc: 0,
    schedule_charge_duration: 0,
    schedule_charge_day: -7,
    max_discharge_power: -1,
    max_charge_power: patBlockCharge ? 0 : -1,
    blockMinSoc: effectiveMinSoc,
    feedin_on: true,
    max_feed_in_power: 7600,
    prevent_feedback: 0
};
msg.mode = "prodavat";
msg.feedInExcess = true;
node.status({fill:"red", shape:"dot", text:"Prod├ívat (solar) | SOC: " + currentSoc + "% (min:" + effectiveMinSoc + "%)"});

var consumers = [];
if (msg.cerpadloTopi) consumers.push("Topeni");
if (msg.saunaAktivni) consumers.push("Sauna");
if (msg.autoNabijeniAktivni) consumers.push("Auto");

global.set("energy_arbiter", {
    mode: "prodavat",
    battery_charging: false,
    reserved_for_battery_w: 0,
    max_discharge_allowed: true,
    consumers_active: consumers,
    timestamp: Date.now()
});

return msg;

var patBlockCharge = global.get("pat_block_charge") || false;
var saunaAktivni = msg.saunaAktivni || false;
var config = global.get("fve_config") || {};
var status = global.get("fve_status") || {};
var currentSoc = status.battery_soc || 50;
var minSoc = config.min_soc || 20;

// === ULTRA LEVNA DETEKCE ===
var prices = global.get("fve_prices_forecast") || [];
var hour = new Date().getHours();
var plan = global.get("fve_plan") || {};
var solarStart = (plan && plan.solarStartHour) || 9;
var solarEnd = (plan && plan.solarEndHour) || 17;
var isSolarHour = (hour >= solarStart && hour < solarEnd);
var PRAH_ULTRA_LEVNA = config.prah_ultra_levna_nakup || 1.0;

var currentBuyPrice = 99;
for (var i = 0; i < prices.length; i++) {
    if (prices[i].hour === hour && (prices[i].minute === 0 || prices[i].minute === undefined)) {
        currentBuyPrice = parseFloat(prices[i].priceCZKhourBuy) || 99;
        break;
    }
}

var ultraLevna = isSolarHour && currentBuyPrice < PRAH_ULTRA_LEVNA;
global.set("ultra_levna_energie", ultraLevna);

msg.victron = {
    power_set_point: 100,
    min_soc: minSoc,
    schedule_soc: 0,
    schedule_charge_duration: 0,
    schedule_charge_day: -7,
    max_discharge_power: -1,
    max_charge_power: patBlockCharge ? 0 : -1,
    blockMinSoc: minSoc,
    feedin_on: false,
    max_feed_in_power: 0,
    prevent_feedback: 1
};
msg.mode = "zakaz_pretoku";
var autoText = msg.autoNabijeniAktivni ? " + Auto" : "";

if (ultraLevna) {
    node.status({fill:"yellow", shape:"dot", text:"ULTRA LEVNA " + currentBuyPrice.toFixed(2) + " Kc" + autoText + " | SOC " + currentSoc + "%"});
} else {
    node.status({fill:"purple", shape:"dot", text:"Zakaz pretoku" + autoText + " | SOC " + currentSoc + "%"});
}

var consumers = [];
if (msg.cerpadloTopi) consumers.push("Topeni");
if (msg.saunaAktivni) consumers.push("Sauna");
if (msg.autoNabijeniAktivni) consumers.push("Auto");
if (ultraLevna) consumers.push("UltraLevna");

global.set("energy_arbiter", {
    mode: ultraLevna ? "ultra_levna" : "zakaz_pretoku",
    battery_charging: false,
    max_discharge_allowed: true,
    consumers_active: consumers,
    timestamp: Date.now()
});

return msg;

var patBlockCharge = global.get("pat_block_charge") || false;
var config = global.get("fve_config") || {};
var status = global.get("fve_status") || {};
var minSoc = config.min_soc || 20;
var currentSoc = status.battery_soc || 50;
var cerpadloTopi = msg.cerpadloTopi || false;
var autoCharging = msg.autoNabijeniAktivni || false;
var saunaAktivni = msg.saunaAktivni || false;
var minDischargeBlokaceW = config.min_vybijeni_blokace_w || 1300;

var pvFveEntity = global.get("homeassistant.homeAssistant.states['sensor.vyroba_fve']") || {};
var currentPvPower = parseFloat(pvFveEntity.state) || 0;
var dynamicDischargeW = Math.max(0, Math.round(minDischargeBlokaceW - currentPvPower));

var nibeFromGrid = cerpadloTopi && currentPvPower < 500;
var blockDischarge = nibeFromGrid || saunaAktivni;

msg.victron = {
    power_set_point: 0,
    min_soc: minSoc,
    schedule_soc: 0,
    schedule_charge_duration: 0,
    schedule_charge_day: -7,
    max_discharge_power: -1,
    max_charge_power: patBlockCharge ? 0 : -1,
    blockMinSoc: blockDischarge ? Math.min(currentSoc + 1, 100) : minSoc,
    feedin_on: true,
    max_feed_in_power: 7600,
    prevent_feedback: 0
};
msg.mode = "solar_charging";
msg.blockDischarge = blockDischarge;
msg.maxDischargePower = -1;
msg.blockMinSoc = msg.victron.blockMinSoc;

var consumers = [];
if (cerpadloTopi) consumers.push(nibeFromGrid ? "NIBE(sit)" : "NIBE(sol)");
if (autoCharging) consumers.push("Auto(sol)");
if (saunaAktivni) consumers.push("Sauna");
var consText = consumers.length > 0 ? " | " + consumers.join("+") : "";

node.status({
    fill: "yellow",
    shape: "ring",
    text: "SolarÔÜí" + consText + " | SOC: " + currentSoc + "%" + (blockDischarge ? " (BAT LOCKED)" : "")
});

global.set("energy_arbiter", {
    mode: "solar_charging",
    battery_charging: !blockDischarge,
    battery_discharging: !blockDischarge,
    reserved_for_battery_w: 0,
    max_discharge_allowed: !blockDischarge,
    consumers_active: consumers,
    timestamp: Date.now()
});

return msg;

// Fan-out: p┼Öe─Źte msg.victron a nastav├ş msg properties pro Mustache templates
var v = msg.victron || {};
msg.victron = v;

// Feed-in control
msg.feedin_on = (v.feedin_on !== false);
msg.feedin_max = (v.max_feed_in_power !== undefined) ? v.max_feed_in_power : 7600;
msg.prevent_feedback = v.prevent_feedback || 0;

return msg;

// FVE Logger - pripravi log radek
var arbiter = global.get("energy_arbiter") || {};
var config  = global.get("fve_config") || {};
var status  = global.get("fve_status") || {};
var pvState  = global.get("homeassistant.homeAssistant.states['sensor.fve_dostupny_prebytek']") || {};
var nibeState = global.get("homeassistant.homeAssistant.states['switch.nibe_topeni']") || {};

var entry = {
    ts: new Date().toISOString(),
    mode: msg.mode || arbiter.mode || "?",
    soc: status.battery_soc || 0,
    prebytek_w: parseFloat(pvState.state) || 0,
    block_discharge: msg.blockDischarge || false,
    block_min_soc: msg.blockMinSoc || config.min_soc || 20,
    nibe: nibeState.state || "?",
    topeni_mod: global.get("topeni_mod") || "?",
    consumers: (arbiter.consumers_active || []).join("+") || "-"
};

msg.payload = JSON.stringify(entry);
return msg;

// FVE Logger - rotace souboru (odstran zaznamy starsi 3 dny)
var fs = require('fs');
var logFile = "/homeassistant/fve_log.jsonl";
var cutoff = new Date(Date.now() - 3 * 24 * 60 * 60 * 1000).toISOString();

try {
    var existing = "";
    try { existing = fs.readFileSync(logFile, "utf8"); } catch(e) {}
    var lines = existing.split("\n").filter(function(l) {
        if (!l.trim()) return false;
        try { return JSON.parse(l).ts >= cutoff; } catch(e) { return false; }
    });
    if (lines.length > 0) {
        fs.writeFileSync(logFile, lines.join("\n") + "\n", "utf8");
        node.status({fill:"green", shape:"dot", text: lines.length + " radku zachovano"});
    }
} catch(e) {
    node.warn("Rotace log error: " + e.message);
}
return null;
