var config = global.get("fve_config") || {};
var status = global.get("fve_status") || {};
var prices = global.get("fve_prices_forecast") || [];
var forecastZitra = global.get("forecast_vyroba_zitra") || 0;
var zbyvajiciSolar = global.get("zbyvajici_solar_dnes") || 0;
var consumptionAnalysis = global.get("fve_consumption_analysis") || {};

msg.planData = {
    config: config,
    status: status,
    prices: prices,
    forecast: {
        zitra: forecastZitra
    },
    zbyvajiciSolar: zbyvajiciSolar,
    consumptionAnalysis: consumptionAnalysis,
    timestamp: new Date().toISOString()
};

node.status({fill:"blue", shape:"dot", text:"Solar: " + Math.round(zbyvajiciSolar) + " kWh"});
return msg;

// FVE Smart Plan v6 - Striktní prahový systém
// Cíl: Nejnižší denní platby za elektřinu
// Strategie:
//   Level ≤ PRAH_LEVNA          → NABIJET (pokud třeba) nebo SETRIT
//   PRAH_LEVNA < level ≤ efPRAH → SETRIT (střední cena, šetři baterii)
//   Level > efPRAH              → NORMAL (drahá energie, používej baterii)
// PRAVIDLO: efPRAH NIKDY neklesne pod konfigurovanou PRAH_DRAHA!
// Dynamika jen NAHORU (více SETRIT) pokud:
//   - Baterie nepotřebuje tolik vybíjet
//   - Solár pokryje spotřebu
// Runtime úpravy (v Kontrola podmínek) řeší aktuální spotřebu a solár

var data = msg.planData;
var config = data.config || {};
var status = data.status || {};
var prices = data.prices || [];
var forecast = data.forecast || {};

var MODY = {
    NORMAL: "normal",
    SETRIT: "setrit",
    NABIJET_ZE_SITE: "nabijet_ze_site",
    PRODAVAT: "prodavat",
    ZAKAZ_PRETOKU: "zakaz_pretoku"
};

// === PARAMETRY Z CONFIGU ===
var kapacitaBaterie = config.kapacita_baterie_kwh || 28;
var minSoc = config.min_soc || 20;
var amortizace = config.amortizace_baterie_czk_kwh || 1.0;
var prodejZBaterieEnabled = config.prodej_z_baterie_enabled === true;
var blokaceVybijeni = config.blokace_vybijeni === true;
var manualMod = config.manual_mod || "auto";
var horizont = config.plan_horizon_hours || 12;
var maxGridW = config.max_spotreba_sit_w || 22000;
var PRAH_LEVNA = config.prah_levna_energie || 4;
var PRAH_DRAHA = config.prah_draha_energie || 12;
var BATTERY_EFFICIENCY = 0.95;
var CHARGE_RATE_KWH = 5;
var SOC_DROP_NORMAL = 5; // % SOC pokles za hodinu v NORMAL módu

// === AKTUÁLNÍ STAV ===
var currentSoc = status.battery_soc || 50;
var forecastZitra = forecast.zitra || 0;
var remainingSolarKwh = data.zbyvajiciSolar || 0;

// Sledování plného nabití (údržba baterie)
var lastFullCharge = global.get("fve_last_full_charge") || null;
var daysSinceFullCharge = lastFullCharge
    ? Math.floor((Date.now() - new Date(lastFullCharge).getTime()) / (1000 * 60 * 60 * 24))
    : 999;
if (currentSoc >= 99) {
    global.set("fve_last_full_charge", new Date().toISOString());
}

var now = new Date();
var currentHour = now.getHours();

// === ODHAD DENNÍ SPOTŘEBY ===
var dailyConsumptionKwh = 20;

// === VÝPOČET: KOLIK ENERGIE POTŘEBUJI ZE SÍTĚ? ===
var batteryNeedKwh = ((100 - currentSoc) / 100) * kapacitaBaterie / BATTERY_EFFICIENCY;
var hoursLeftToday = Math.max(0, 24 - currentHour);
var consumptionRemaining = dailyConsumptionKwh * hoursLeftToday / 24;
var solarForBattery = Math.max(0, remainingSolarKwh - consumptionRemaining);
var gridChargeNeeded = Math.max(0, batteryNeedKwh - solarForBattery);
var targetSocFromGrid = Math.min(100, currentSoc + Math.round(gridChargeNeeded / kapacitaBaterie * 100));

// Pokud solár pokryje potřebu, nenabíjet ze sítě
if (targetSocFromGrid <= currentSoc + 3) {
    gridChargeNeeded = 0;
    targetSocFromGrid = currentSoc;
}

// Údržbové nabíjení každých 10 dní
var maintenanceCharge = false;
if (daysSinceFullCharge >= 10 && targetSocFromGrid < 100) {
    targetSocFromGrid = 100;
    gridChargeNeeded = batteryNeedKwh;
    maintenanceCharge = true;
}

var hoursNeeded = Math.ceil(gridChargeNeeded / CHARGE_RATE_KWH);

// === POMOCNÉ FUNKCE ===
function getPriceDataForHour(hour) {
    for (var i = 0; i < prices.length; i++) {
        if (prices[i].hour === hour) {
            return {
                buy: prices[i].priceCZKhourBuy || 0,
                sell: prices[i].priceCZKhourProd || 0,
                levelBuy: prices[i].levelCheapestHourBuy || 5,
                levelSell: prices[i].levelMostExpensiveHourProd || 5
            };
        }
    }
    return { buy: 0, sell: 0, levelBuy: 5, levelSell: 5 };
}

// === KROK 1: CENOVÁ MAPA HORIZONTU ===
var hourPrices = [];
for (var i = 0; i < horizont; i++) {
    var h = (currentHour + i) % 24;
    var pd = getPriceDataForHour(h);
    hourPrices.push({
        offset: i,
        hour: h,
        buy: pd.buy,
        levelBuy: pd.levelBuy,
        sell: pd.sell,
        levelSell: pd.levelSell
    });
}

// === KROK 2: NABÍJECÍ HODINY (level ≤ PRAH_LEVNA, nejlevnější) ===
var cheapHours = hourPrices.filter(function(hp) { return hp.levelBuy <= PRAH_LEVNA; });
cheapHours.sort(function(a, b) { return a.buy - b.buy; });

var chargingOffsets = {};
var assignedHours = 0;
for (var ci = 0; ci < cheapHours.length && assignedHours < hoursNeeded; ci++) {
    chargingOffsets[cheapHours[ci].offset] = true;
    assignedHours++;
}

// Fallback: pokud nestačí levné hodiny, rozšíř na nejlevnější zbylé
if (assignedHours < hoursNeeded) {
    var remaining = hourPrices.filter(function(hp) { return !chargingOffsets[hp.offset]; });
    remaining.sort(function(a, b) { return a.buy - b.buy; });
    for (var ri = 0; ri < remaining.length && assignedHours < hoursNeeded; ri++) {
        chargingOffsets[remaining[ri].offset] = true;
        assignedHours++;
    }
}

// === KROK 3: DYNAMICKÝ PRAH_DRAHA ===
// Cíl: dostat SOC na minSOC těsně (~30 min) před prvním nabíjecím oknem
// Zjisti kolik hodin NORMAL potřebuji a přizpůsob práh

// Najdi offset první nabíjecí hodiny
var firstChargingOffset = horizont;
for (var fco = 0; fco < horizont; fco++) {
    if (chargingOffsets[fco]) { firstChargingOffset = fco; break; }
}

// Kolik SOC musím vybít? (cíl: minSOC před nabíjením)
var socToDischarge = Math.max(0, currentSoc - minSoc);
var normalHoursNeeded = Math.ceil(socToDischarge / SOC_DROP_NORMAL);

// Spočítej ne-nabíjecí hodiny PŘED nabíjecím oknem a jejich levely
var preChargeHours = [];
for (var pc = 0; pc < firstChargingOffset; pc++) {
    if (!chargingOffsets[pc]) {
        preChargeHours.push(hourPrices[pc]);
    }
}

// Historická korekce z učení
// Pokud se baterie v minulých cyklech nevybila na minSOC → snížit práh
var histCorrection = global.get("fve_prah_correction") || 0;

// Funkce: spočítej kolik hodin NORMAL by bylo při daném prahu
function countNormalHours(threshold) {
    var count = 0;
    for (var cn = 0; cn < preChargeHours.length; cn++) {
        if (preChargeHours[cn].levelBuy > threshold) {
            count++;
        }
    }
    return count;
}

// Dynamická úprava PRAH_DRAHA - NIKDY pod konfigurovanou hodnotu!
var effectivePrahDraha = Math.max(PRAH_DRAHA, PRAH_DRAHA + histCorrection);
var normalAvailable = countNormalHours(effectivePrahDraha);

// Pokud máme příliš mnoho NORMAL hodin → zvýšit práh (víc hodin bude SETRIT)
if (normalAvailable > normalHoursNeeded + 1 && normalHoursNeeded > 0) {
    while (effectivePrahDraha < 20 && normalAvailable > normalHoursNeeded + 1) {
        effectivePrahDraha++;
        normalAvailable = countNormalHours(effectivePrahDraha);
    }
}

// Solar awareness: pokud solár pokryje většinu spotřeby, šetři baterii víc
if (remainingSolarKwh > consumptionRemaining * 0.7) {
    effectivePrahDraha = Math.min(20, effectivePrahDraha + 2);
    normalAvailable = countNormalHours(effectivePrahDraha);
}

// Omezení: efektivní práh NIKDY pod PRAH_DRAHA, max 20
effectivePrahDraha = Math.max(PRAH_DRAHA, Math.min(20, effectivePrahDraha));

// === KROK 3b: HISTORICKÉ UČENÍ ===
// Po každém nabíjecím cyklu zkontroluj zda SOC před nabíjením odpovídal cíli
var planLog = global.get("fve_plan_log") || [];
var lastChargingSoc = global.get("fve_last_charging_soc") || null;

// Detekce: právě jsme přešli do nabíjení (předchozí hodina nebyla nabíjecí)
if (chargingOffsets[0] && lastChargingSoc === null) {
    // Ukládám SOC na začátku nabíjení
    global.set("fve_last_charging_soc", currentSoc);
} else if (!chargingOffsets[0] && lastChargingSoc !== null) {
    // Nabíjení skončilo → vyhodnotit
    var socAtChargeStart = lastChargingSoc;
    var targetWas = minSoc;
    var diff = socAtChargeStart - targetWas;

    if (diff > 10) {
        // Baterie se nevybila dostatečně → snížit práh pro příště
        histCorrection = Math.max(-5, histCorrection - 1);
        global.set("fve_prah_correction", histCorrection);
    } else if (diff < 3) {
        // Baterie se vybila přesně nebo moc → zvýšit práh (menší vybíjení)
        histCorrection = Math.min(5, histCorrection + 1);
        global.set("fve_prah_correction", histCorrection);
    }
    // Reset pro další cyklus
    global.set("fve_last_charging_soc", null);
}

// === KROK 4: VÝPOČET MÓDU PRO KAŽDOU HODINU ===
function calculateModeForHour(offset, priceData, simulatedSoc) {
    if (manualMod !== "auto") {
        return { mode: manualMod, reason: "Manuální mód" };
    }

    var priceBuy = priceData.buy;
    var priceSell = priceData.sell;
    var levelBuy = priceData.levelBuy;
    var levelSell = priceData.levelSell;

    // PRIORITA 0: Záporná prodejní cena → zákaz přetoků
    if (priceSell <= 0) {
        return { mode: MODY.ZAKAZ_PRETOKU, reason: "Záporná cena (" + priceSell.toFixed(2) + " Kč)" };
    }

    // PRIORITA 1: Nabíjení ze sítě
    if (chargingOffsets[offset] && simulatedSoc < targetSocFromGrid) {
        return {
            mode: MODY.NABIJET_ZE_SITE,
            reason: "Nabíjení (" + priceBuy.toFixed(2) + " Kč, level " + levelBuy + ", cíl " + targetSocFromGrid + "%)"
        };
    }

    // PRIORITA 2: Prodej z baterie při výhodné ceně
    if (prodejZBaterieEnabled && levelSell >= 15 && simulatedSoc > minSoc + 20) {
        var cistyZisk = (priceSell * BATTERY_EFFICIENCY) - amortizace;
        if (cistyZisk > priceBuy * 0.8) {
            return {
                mode: MODY.PRODAVAT,
                reason: "Výhodný prodej (" + priceSell.toFixed(2) + " Kč)"
            };
        }
    }

    // PRIORITA 3: Ochrana baterie - příliš nízký SOC
    if (simulatedSoc <= minSoc + 3) {
        return { mode: MODY.SETRIT, reason: "Ochrana baterie (SOC " + Math.round(simulatedSoc) + "%)" };
    }

    // PRIORITA 4: TŘÍZONOVÝ PRAHOVÝ SYSTÉM
    // Zóna 1: Level ≤ PRAH_LEVNA → levná energie → šetřit baterii
    if (levelBuy <= PRAH_LEVNA) {
        return {
            mode: MODY.SETRIT,
            reason: "Levná energie (level " + levelBuy + " ≤ " + PRAH_LEVNA + ")"
        };
    }

    // Zóna 2: PRAH_LEVNA < level ≤ efektivní PRAH_DRAHA → střední cena → šetřit
    if (levelBuy <= effectivePrahDraha) {
        return {
            mode: MODY.SETRIT,
            reason: "Střední cena (level " + levelBuy + " ≤ efPráh " + effectivePrahDraha + ")"
        };
    }

    // Zóna 3: Level > efektivní PRAH_DRAHA → drahá energie → používat baterii
    return {
        mode: MODY.NORMAL,
        reason: "Drahá energie (level " + levelBuy + " > efPráh " + effectivePrahDraha + ", " + priceBuy.toFixed(2) + " Kč)"
    };
}

// === SIMULACE SOC ===
function simulateSocChange(mode, hour, soc) {
    var isSolar = hour >= 9 && hour < 17;
    switch (mode) {
        case MODY.NABIJET_ZE_SITE:
            return Math.min(100, soc + (CHARGE_RATE_KWH / kapacitaBaterie * 100 * BATTERY_EFFICIENCY));
        case MODY.PRODAVAT:
            return Math.max(minSoc, soc - (15 / BATTERY_EFFICIENCY / kapacitaBaterie * 100));
        case MODY.SETRIT:
        case MODY.ZAKAZ_PRETOKU:
            return Math.max(minSoc, soc - 1);
        case MODY.NORMAL:
            if (isSolar && remainingSolarKwh > 0) {
                return Math.min(100, soc + 3);
            }
            return Math.max(minSoc, soc - SOC_DROP_NORMAL);
        default:
            return soc;
    }
}

// === GENEROVÁNÍ PLÁNU ===
var modeNamesCZ = {
    normal: "Normální provoz",
    setrit: "Šetřit baterii",
    nabijet_ze_site: "Nabíjet ze sítě",
    prodavat: "Prodávat do sítě",
    zakaz_pretoku: "Zákaz přetoků"
};

var plan = [];
var simulatedSoc = currentSoc;

for (var pi = 0; pi < horizont; pi++) {
    var planHour = (currentHour + pi) % 24;
    var isNextDay = (currentHour + pi) >= 24;
    var priceData = getPriceDataForHour(planHour);
    var result = calculateModeForHour(pi, priceData, simulatedSoc);
    simulatedSoc = simulateSocChange(result.mode, planHour, simulatedSoc);
    plan.push({
        hour: planHour,
        offset: pi,
        mode: result.mode,
        modeCZ: modeNamesCZ[result.mode] || result.mode,
        reason: result.reason,
        priceLevel: priceData.levelBuy,
        priceBuy: priceData.buy,
        priceSell: priceData.sell,
        simulatedSoc: Math.round(simulatedSoc),
        isNextDay: isNextDay,
        isChargingHour: chargingOffsets[pi] || false
    });
}

// === LOGOVÁNÍ PLÁNU ===
var logEntry = {
    timestamp: now.toISOString(),
    hour: currentHour,
    soc: currentSoc,
    effectivePrahDraha: effectivePrahDraha,
    histCorrection: histCorrection,
    normalHoursNeeded: normalHoursNeeded,
    normalHoursAvailable: normalAvailable,
    remainingSolar: remainingSolarKwh,
    gridChargeNeeded: Math.round(gridChargeNeeded * 10) / 10,
    plan: plan.map(function(p) {
        return { h: p.hour, m: p.mode, l: p.priceLevel, soc: p.simulatedSoc };
    })
};
planLog.push(logEntry);
if (planLog.length > 48) { planLog = planLog.slice(-48); }
global.set("fve_plan_log", planLog);

// === VÝSTUP ===
var currentMode = plan.length > 0 ? plan[0].mode : MODY.NORMAL;
var currentReason = plan.length > 0 ? plan[0].reason : "";

var countSetrit = 0, countNormal = 0, countNabijet = 0;
for (var cs = 0; cs < plan.length; cs++) {
    if (plan[cs].mode === MODY.SETRIT) countSetrit++;
    else if (plan[cs].mode === MODY.NORMAL) countNormal++;
    else if (plan[cs].mode === MODY.NABIJET_ZE_SITE) countNabijet++;
}

msg.payload = {
    currentMode: currentMode,
    currentReason: currentReason,
    currentHour: currentHour,
    plan: plan,
    config: { prah_levna: PRAH_LEVNA, prah_draha: PRAH_DRAHA },
    dynamicThreshold: {
        configPrahDraha: PRAH_DRAHA,
        effectivePrahDraha: effectivePrahDraha,
        histCorrection: histCorrection,
        normalHoursNeeded: normalHoursNeeded,
        normalHoursAvailable: normalAvailable,
        firstChargingOffset: firstChargingOffset
    },
    smartCharging: {
        targetSocFromGrid: targetSocFromGrid,
        gridChargeNeeded: Math.round(gridChargeNeeded * 10) / 10,
        hoursNeeded: hoursNeeded,
        remainingSolar: Math.round(remainingSolarKwh * 10) / 10,
        solarForBattery: Math.round(solarForBattery * 10) / 10,
        dailyConsumption: dailyConsumptionKwh,
        batteryNeed: Math.round(batteryNeedKwh * 10) / 10,
        daysToFullCharge: daysSinceFullCharge,
        maintenanceCharge: maintenanceCharge
    },
    planSummary: {
        setritHours: countSetrit,
        normalHours: countNormal,
        nabijetHours: countNabijet
    },
    status: {
        soc: currentSoc,
        remainingSolar: remainingSolarKwh,
        forecastZitra: forecastZitra,
        prodejEnabled: prodejZBaterieEnabled,
        blokaceVybijeni: blokaceVybijeni
    },
    generatedAt: now.toISOString()
};

var modeColors = { normal: "green", setrit: "yellow", nabijet_ze_site: "blue", prodavat: "red", zakaz_pretoku: "purple" };
node.status({
    fill: modeColors[currentMode] || "grey",
    shape: "dot",
    text: currentMode + " | efPráh:" + effectivePrahDraha + "(cfg:" + PRAH_DRAHA + " korekce:" + histCorrection + ") | S:" + countSetrit + " N:" + countNormal + " Nab:" + countNabijet + " | SOC:" + currentSoc + "%→" + targetSocFromGrid + "%"
});

return msg;

global.set("fve_plan", msg.payload);
global.set("fve_current_mode", msg.payload.currentMode);

msg.ha_update = {
    state: msg.payload.currentMode,
    attributes: {
        current_mode: msg.payload.currentMode,
        current_reason: msg.payload.currentReason,
        current_hour: msg.payload.currentHour,
        plan: msg.payload.plan,
        soc: msg.payload.status.soc,
        forecast_dnes: msg.payload.status.forecastDnes,
        forecast_zitra: msg.payload.status.forecastZitra,
        last_update: msg.payload.generatedAt
    }
};

return msg;

var planData = msg.payload || {};
var currentMode = planData.currentMode || "normal";
var currentHour = planData.currentHour || new Date().getHours();
var planArray = planData.plan || [];

node.status({fill:"green", shape:"dot", text: "Mód: " + currentMode + ", hodina: " + currentHour});

msg.payload = JSON.stringify({
    current_mode: currentMode,
    current_hour: currentHour,
    plan: planArray,
    last_update: new Date().toISOString()
}, null, 2);

return msg;

var config = global.get("fve_config") || {};
var plan = global.get("fve_plan") || {};
var planMode = global.get("fve_current_mode") || "normal";
var manualMod = config.manual_mod || "auto";

var currentMode = planMode;
if (manualMod !== "auto") {
    currentMode = manualMod;
}

var wasEnabled = flow.get("automatizace_was_enabled") || false;
if (config.automatizace_enabled === false) {
    if (wasEnabled) {
        flow.set("automatizace_was_enabled", false);
        global.set("fve_current_mode", "normal");
        msg.currentMode = "normal";
        msg.doReset = true;
        node.status({fill:"yellow", shape:"dot", text:"Reset do normalniho modu"});
        return msg;
    }
    node.status({fill:"grey", shape:"ring", text:"Automatizace vypnuta"});
    return null;
}
flow.set("automatizace_was_enabled", true);

var autoNabijeni = global.get("auto_nabijeni_aktivni") || false;
var cerpadloTopi = global.get("cerpadlo_topi") || false;

// Ochrana baterie: pri topeni/TUV nebo nabijeni auta blokovat vybijeni
if ((cerpadloTopi || autoNabijeni) && currentMode === "normal") {
    currentMode = "setrit";
}

msg.autoNabijeniAktivni = autoNabijeni;
msg.cerpadloTopi = cerpadloTopi;
msg.currentMode = currentMode;
msg.config = config;
msg.plan = plan;

var modeSource = manualMod !== "auto" ? "(manualni)" : "(auto)";
var protectText = "";
if (cerpadloTopi) protectText += " | Cerpadlo topi";
if (autoNabijeni) protectText += " | Auto nabiji";
node.status({fill: manualMod !== "auto" ? "blue" : "green", shape:"dot", text: "Mod: " + currentMode + " " + modeSource + protectText});

return msg;

var mode = msg.currentMode;
var skipBattery = msg.skipBatteryControl || false;

switch (mode) {
    case "normal": msg.modeIndex = 0; break;
    case "setrit": msg.modeIndex = 1; break;
    case "nabijet_ze_site": msg.modeIndex = 2; break;
    case "prodavat": msg.modeIndex = 3; break;
    case "zakaz_pretoku": msg.modeIndex = 4; break;
    default: msg.modeIndex = 0;
}

msg.skipBatteryControl = skipBattery;
return msg;

var state = msg.payload;
var chargingStates = ["Charging", "Nabíjení", "2"];
var isCharging = chargingStates.some(function(s) {
    return state.toLowerCase().indexOf(s.toLowerCase()) !== -1 || state === s;
});
msg.autoNabijeni = isCharging;
node.status({fill: isCharging ? "yellow" : "green", shape:"dot", text: isCharging ? "Auto se nabíjí" : "Auto se nenabíjí"});
return msg;
